sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    var recordNumber = request.pathParams.number;

    var gRec = new GlideRecord('x_869126_sams_student_details');
    gRec.addQuery('number', recordNumber);
    gRec.query();

    if (gRec.next()) {
        response.setStatus(200);
        response.setBody({
            "status": "SUCCESS",
            "details": {
                "sys_id": gRec.sys_id,
                "First Name": gRec.getDisplayValue('name'),
                "Last Name": gRec.getDisplayValue('last_name'),
                "Contact Detais": {
                    "Contact Number": gRec.getDisplayValue('contact_number'),
                    "Email": gRec.getDisplayValue('email'),
                }
            }
        });
    } else {
        response.setStatus(404);
        response.setBody({
            "status": 'ERROR',
            "message": 'Incident not found'
        });
        // response.setError(400, 'Incident not found');
    }

})(request, response);
