sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    var requestBody = request.body;
    var requestString = requestBody.dataString;
    var requestData = JSON.parse(requestString);

    var mandatoryFields = [];

    var firstName = requestData.firstName;
    var lastName = requestData.lastName;
    var contactNumber = requestData.contactNumber;

    if (!firstName) mandatoryFields.push('firstName');

    if (!lastName) mandatoryFields.push('lastName');

    if (!contactNumber) mandatoryFields.push('contactNumber');


    if (mandatoryFields.length > 0) {
        var errorMessage;
        mandatoryFields.length > 1 ? errorMessage = mandatoryFields.join(', ') + ' are mandatory' : errorMessage = mandatoryFields.join(', ') + ' is mandatory';
        var responseBody = {
            "status": "ERROR",
            "message": errorMessage
        };
        response.setStatus(400);
        response.setBody(responseBody);
        return;
    }

    var gRec = new GlideRecord('x_869126_sams_student_details');
    gRec.initialize();
    gRec.name = firstName;
    gRec.last_name = lastName;
    gRec.contact_number = contactNumber;
    gRec.insert();

    var responseObj = {
        "status": "SUCCESS",
        "message": 'Record Created',
        "details": {
            "record": gRec.getDisplayValue('number')
        }
    };

    response.setStatus(201);
    response.setBody(responseObj);

})(request, response);
